import s from './news.module.css';
import React, { useRef, useEffect, useState, useMemo, useCallback } from 'react';
import ItemsList from './items.list';



const News = (props) => {

    const [colored, setColored] = useState(false)
    const [count, setCount] = useState(1);

    const styles = {
        color: colored ? 'red' : 'black'
    }

    const generateItemFromApi = useCallback(() => {
        return new Array(count).fill('').map((_, i) => `Элемент ${i + 1}`)
    },[])

    return (
        <div>
            <h1 style={styles}>Колво элементов:{count}</h1>
            <button onClick={() => setCount(prev => prev + 1)} >Добавить</button>
            <button onClick={() => setColored(prev => !prev)}> Изменить</button>
            <ItemsList getItems={generateItemFromApi} />
        </div >
    )

}

export default News;
