import React, { useContext } from "react";
import { AlertContext, useAlert } from './alertContext'

const Alert = () => {
    const alert = useAlert
    if (!alert) return null

    return (
        <div className={'alert-danger'}>
            Это важное сообщение
        </div>

    )
}




export default Alert
